    ,animateNumbers : function(){
        var elem;
        if($(window).width()>1025){
            elem = $('.awards-logos-box.desktop_only')
        }else{
            // elem = $('.awards-logos-box.mobile_only')
        } 
        if($(elem).length){
    
            var awards = $(elem).find('.awards-logo').find('.awards-count');
            $(awards).each(function () {
                var startCount = parseInt( $(this).data('start'));
                var endCount   = parseInt( $(this).data('end'));
                $(this).prop('Counter',startCount).animate({
                    Counter: endCount
                }, {
                    duration: 0,
                    easing: 'swing',
                    step: function (now) {
                        var num =  Math.ceil(now);
                        if(num <= 9 ){ num = "0" + num}
                        $(this).text(num);
                    }
                });
            });
        }
    }
