App.CasestudyMixGallery = {

    imageTimer : 4000
    ,stopAutoPlay : false


    ,init :function(){

        if( !$('.casestudy-gallery').length) return;

        if( _controller == 'home' ){
            $('.casestudy-gallery').each(function(){
                App.CasestudyMixGallery.configGallery(this);
                App.CasestudyMixGallery.updateInSection();
            })
        }
    }


    ,configGallery : function(gallery){

        if ( App.CasestudyMixGallery.stopAutoPlay ) return;
        var galleryItemsCount = $($(gallery).find('.casestudy-gallery-item')).length;
        var activeGalleryItem = $(gallery).find('.casestudy-gallery-item.active');
        var activeGalleryItemID = $(activeGalleryItem).attr('id');
        var activeGalleryItemIndex = $(activeGalleryItem).index();

        if( galleryItemsCount == 1 ){
            console.log('only one item in gallery');
            if(App.CasestudyMixGallery.isVideo( activeGalleryItem )){
                console.log('only one video in gallery');
                App.CasestudyMixGallery.playSingleVideo(activeGalleryItemID, gallery, activeGalleryItem);
            }
            return;
        }

        if( !App.CasestudyMixGallery.isVideo( activeGalleryItem ) ){
            App.CasestudyMixGallery.showNextImage( gallery, activeGalleryItem );
        }else{
            App.CasestudyMixGallery.playVideo( activeGalleryItemID, gallery, activeGalleryItem );
        }
    }


    ,playVideo : function( videoId, gallery, activeElem ){
        if ( App.CasestudyMixGallery.stopAutoPlay ) return;
        var video = document.getElementById(videoId);
        var playPromise = $(video).get(0).play();
        if (playPromise !== undefined){

            playPromise.then(function() {
                video.play(); 
            })
            .catch(function() {
                video.play(); 
            });
        }
        video.onended = function(e) {
            App.CasestudyMixGallery.play(gallery,activeElem);
        };
    }

    ,
    updateInSection: function() {
        
        if ($(".casestudies-grid-project").length) {
          var controller = new ScrollMagic.Controller(),
            elem = $(".casestudies-grid-project");

          $(elem).each(function (index, el) {
            var tween = new TimelineMax({
              paused: true,
            });

            tween.from(".foo", 2, { x: "400", duration: 0, autoAlpha: 0 });
            tween.play();

            var scene = new ScrollMagic.Scene({
              triggerElement: el,
              reverse: false,
              triggerHook: "onEnter",
            })
              .setTween(tween)
              .addTo(controller);

            scene.on("enter", function (event) {
              var gallery = el;
              var galleryItemsCount = $($(gallery).find(".casestudy-gallery-item")).length;
              var activeGalleryItem = $(gallery).find(".casestudy-gallery-item.active");
              var activeGalleryItemID = $(activeGalleryItem).attr("id");

              if (galleryItemsCount == 1) {
                if (App.CasestudyMixGallery.isVideo(activeGalleryItem)) {
                  App.CasestudyMixGallery.playSingleVideo(
                    activeGalleryItemID,
                    ".casestudy-gallery",
                    activeGalleryItem
                  );
                }
              }
            });
          });
        }
    }


    ,playSingleVideo : function(activeGalleryItemID, gallery, activeGalleryItem){
        // console.log('playSingleVideo');
        var video = document.getElementById(activeGalleryItemID);
        $(video).attr("loop", true); 

        var playPromise = $(video).get(0).play();
        if (playPromise !== undefined){

            playPromise.then(function() {
                video.play(); 
            })
            .catch(function() {
                video.play(); 
            });
        }
    }

    
    ,getNextElem : function(gallery,activeGalleryItem){
    
        var nextActiveElem;
        if( $(activeGalleryItem).next('.casestudy-gallery-item') ){
            nextActiveElem = $(activeGalleryItem).next('.casestudy-gallery-item');

            if(!nextActiveElem.length){
                nextActiveElem = $($(gallery).find('.casestudy-gallery-item').eq(0))
            }
        }
        return nextActiveElem;
    }


    ,getId : function(elem){
        return $(elem).attr('id');
    }

    ,mixGallerTL : {}
    ,play : function(gallery,activeGalleryItem){

        var curElem = activeGalleryItem;
        var nextElem = App.CasestudyMixGallery.getNextElem(gallery,activeGalleryItem);
        if(!nextElem) return;
        var video;
        var videoId;
        var tl = new TimelineMax({
            onStart : function(){
                if(App.CasestudyMixGallery.isVideo(nextElem)){

                    videoId = App.CasestudyMixGallery.getId( nextElem )
                    App.CasestudyMixGallery.playVideo(videoId,gallery,nextElem);

                }else{

                    App.CasestudyMixGallery.showNextImage(gallery,nextElem);
                }
            },
            onComplete : function(){
                App.CasestudyMixGallery.stopSiblingsVideos(gallery);
            }
        })

        tl.to(curElem, 0.4, {opacity:0, className:"-=active", ease:Sine.easeOut},0)
        tl.fromTo(nextElem, 0.8, {opacity:0},{opacity:1, className:"+=active", ease:Sine.easeOut},0.2)
        App.CasestudyMixGallery.mixGallerTL = tl;
    }


    ,stopSiblingsVideos : function(gallery){

        var siblingsActiveElem = $(gallery).find('.casestudy-gallery-item.active').siblings();
        $(siblingsActiveElem).each(function(){
            if($(this).is('video')){
                var videoToBeStopped = document.getElementById( App.CasestudyMixGallery.getId($(this)) );
                // console.log(videoToBeStopped, 'videoToBeStopped')
                videoToBeStopped.pause(0);
            }
        })
    }
    

    ,isVideo : function(elem){
        if($(elem).is('video')){
            return true;
        }else{
            return false;
        }
    }


    ,getVideoDuration : function(videoId){
        var video = document.getElementById(videoId);
        return video.duration;
    }

    ,showNextImageTimer : null
    ,showNextImage : function(gallery,nextElem){
        var playNextImageTimer;

        if ( App.CasestudyMixGallery.stopAutoPlay ){
            clearTimeout(playNextImageTimer); 
            return;
        } 
            
        App.CasestudyMixGallery.showNextImageTimer = playNextImageTimer = setTimeout(function(){ 
            App.CasestudyMixGallery.play(gallery,nextElem);
            clearTimeout(playNextImageTimer); 
        }, App.CasestudyMixGallery.imageTimer);
  
    }

    
    ,resetGallery : function(){
        App.CasestudyMixGallery.stopAutoPlay = true;
        if (typeof App.CasestudyMixGallery.mixGallerTL == 'undefined' || App.CasestudyMixGallery.mixGallerTL == null) {
            App.CasestudyMixGallery.mixGallerTL.kill();
        }
        clearTimeout(App.CasestudyMixGallery.showNextImageTimer); 
    }

}
