App.DetectScrollBottom = {


    init : function(){
        App.DetectScrollBottom.bind();
    }


    ,bind : function(){
        $(window).on("scroll",function(){
            App.DetectScrollBottom.detectScrollBottom();
        })
    }


    ,isBottomReached:false
    ,detectScrollBottom : function(){
        var bottomOffset = 200;
        var scrollHeight = $(document).height();
        var scrollPosition = $(window).height() + $(window).scrollTop() + bottomOffset;
        if ( ((scrollHeight - scrollPosition) / scrollHeight) > 0) {
            // when scroll to bottom of the page
            App.DetectScrollBottom.isBottomReached = false;
        }else{
            App.DetectScrollBottom.isBottomReached = true;
        }
    }
}

