App.DragServices = {

  x : 0,
  dragDirection : null,


  bind : function(){
    $(document).on('mousedown touchstart','.draw-circle-svg-3.allowDrag' ,function(e){
      $('.draggable-services.allowDrag').addClass('dragged');
    })

    $(document).on('mouseup touchend', '.draw-circle-svg-3.allowDrag',function(e){
      $('.draggable-services.allowDrag').removeClass('dragged'); 
    })

  }

  ,init : function(){
      if(!$("#scrollVideoBox").length || $(window).width()<1025) return;
      App.DragServices.bind();
      
      if( $(window).width()<1025) return;
      
        var maxDraggableWidth = 275,
            x,
            y,
            minDraggableWidth = 0,
            dragProgress,
            drawImageNumber,
            target;

      App.DragServices.draggableRange;


      interact('.draggable-services')
        .draggable({
          inertia: true,
          autoScroll: true,
          onstart: start,
          onmove: drag,
          onend: restart
        });

        function start(){
          TweenMax.set($('.draggable-services.allowDrag'),{x:0});
        }

        function drag (event) {
          $('.prev-service-slider').prop('disabled', true);
          $('.next-service-slider').prop('disabled', true);
          App.ServiceSlider.removeClickOnTitles();

          $('.circle-cursor--outer').hide();
          if(!App.ServiceSlider.allowDrag) return;
          if( !$(event.target).hasClass("allowDrag") ){
            return;
          } 
          
          //get draggable range px for the active point -  Example: [0,275], [-275,275]
          App.DragServices.draggableRange = App.ServiceSlider.getRange();
          minDraggableWidth = App.DragServices.draggableRange['minWidth'];
          maxDraggableWidth = App.DragServices.draggableRange['maxWidth'];
          nextIndex         = App.DragServices.draggableRange['nextIndex'];
          prevIndex         = App.DragServices.draggableRange['prevIndex'];
          target            = event.target;

          x = (parseFloat(target.getAttribute('data-x')) || 0) + Math.round(event.dx),
          y = (parseFloat(target.getAttribute('data-y')) || 0) + Math.round(event.dy);
        

          App.DragServices.x = x;
          App.DragServices.checkDirection(x);
          if($('.scroll-video-progress.active').index()==3){
            if(x>0) return;
          }

          if($('.scroll-video-progress.active').index()==3){
            if(x>0) return;
          }

          //prevent dragging out of draggable area
          if(x > maxDraggableWidth){
            return;
          } 
          if(x < minDraggableWidth){
            return;
          } 
       
          $('.scroll-video-progress').eq(App.ServiceSlider.getActiveIndex()).addClass("active").siblings().removeClass("act")
          // DRAG DIRECTION - FROM LEFT TO RIGHT
          App.ServiceSlider.setDragEndPointsForDirection(App.DragServices.dragDirection, App.ServiceSlider.getActiveIndex());
          if(App.DragServices.dragDirection == "right"){
            dragProgress = Math.abs(x) / App.DragServices.draggableRange['maxWidth']; // progress between (0,1)
            drawImageNumber = Math.round( App.ServiceSlider.totalImagesBetweenSlides() * dragProgress);
            App.ServiceSlider.drawOnDrag(App.ServiceSlider.drawnImageNumber + drawImageNumber);
            
            if(y>15){y=15}
            if(y<-15){y=-15}
            TweenMax.to($('.draggable-services.allowDrag'), 1, {x:x,y:0});

            target.setAttribute('data-x', x);
            target.setAttribute('data-y', 0);

            $('.scroll-video-progress-dashes-active').removeClass('left');
            App.ServiceSlider.drawRedLine(App.ServiceSlider.getActiveIndex());

            var draggableIndexes = App.ServiceSlider.getRedLinesIndexes(App.ServiceSlider.getActiveIndex())
            if(draggableIndexes.length>1){
              $('.scroll-video-progress-dashes-active').eq(prevIndex).addClass('left');
              App.ServiceSlider.resetRedLine(prevIndex, 0);
            }

          // DRAG DIRECTION - FROM RIGHT TO LEFT

          }else{
            var prevImgNum = parseInt($($('.scroll-video-progress').eq(App.DragServices.draggableRange['prevIndex'])).data('progress'))
            var totalImagesBetweenSlides = App.ServiceSlider.drawnImageNumber - prevImgNum;

            dragProgress = Math.abs(x) / Math.abs(minDraggableWidth); // progress between (0,1)
            drawImageNumber = Math.round( totalImagesBetweenSlides * dragProgress);
            App.ServiceSlider.drawOnDrag(App.ServiceSlider.drawnImageNumber - drawImageNumber);

            if(y>15){y=15}
            if(y<-15){y=-15}
            TweenMax.to($('.draggable-services.allowDrag'), 1, {x:x,y:0});
            target.setAttribute('data-x', x);
            target.setAttribute('data-y', 0);

            $('.scroll-video-progress-dashes-active').addClass('left');
            App.ServiceSlider.drawRedLine(App.DragServices.draggableRange['prevIndex']);

            var draggableIndexes = App.ServiceSlider.getRedLinesIndexes(App.ServiceSlider.getActiveIndex())
            if(draggableIndexes.length>1){
              $('.scroll-video-progress-dashes-active').eq(App.ServiceSlider.getActiveIndex()).removeClass('left');
              App.ServiceSlider.resetRedLine(App.ServiceSlider.getActiveIndex(), 0);
            }
            
          }
        }

        function restart(){
          setTimeout(function(){
            if(!$('.prev-service-slider').hasClass("disabled")){
              $('.prev-service-slider').prop('disabled', false);
            }
            if(!$('.next-service-slider').hasClass("disabled")){
              $('.next-service-slider').prop('disabled', false);
            }
            App.ServiceSlider.addClickOnTitles();
          },2000)


          $('.circle-cursor--outer').show();
      
          if($('.scroll-video-progress.active').index()==3){
            if(x>=0) return;
          }

          if($('.scroll-video-progress.active').index()==0){
            // if(x<=0) return;
            if(x<=0){
              return false;
            } 
          }

          if( App.DragServices.dragDirection == "right" ){
              var activeSlideImages = App.ServiceSlider.getActiveSllideStartEndImg();
              var startImg= activeSlideImages['startImg'];
              var endImg= activeSlideImages['endImg'];

              App.ServiceSlider.drawnImageNumber = App.ServiceSlider.drawnImageNumber + drawImageNumber;
              var nextSlideObj = App.ServiceSlider.getNextSlideIndexAndProgress();
              var nextIndex = nextSlideObj['index'];
              
              if( x >= maxDraggableWidth/2 ){
                //set red line progress to 1
                App.ServiceSlider.resetRedLine(App.ServiceSlider.getActiveIndex(), 1);

                $('.draggable-services').addClass('prevent');

                App.ServiceSlider.resetDragEndPointsForDirection(nextIndex);
                TweenMax.to($('.draggable-services.allowDrag'), 1, {x:maxDraggableWidth,y:0, ease:Sine.easeOut});
                target.setAttribute('data-x', maxDraggableWidth);
                target.setAttribute('data-y', maxDraggableWidth);

                // App.ServiceSlider.allowDrag = false;
                App.ServiceSlider.drawLeftImagesDragProgress(App.ServiceSlider.drawnImageNumber, endImg);

                App.ServiceSlider.reverseSlideAnimation(parseFloat(startImg));
            

                //PLAY
                App.ServiceSlider.timerReverse = setTimeout(function(){
                    App.ServiceSlider.allowDrag = true;
                    App.ServiceSlider.playSlideAnimation(endImg);
                    target.setAttribute('data-x', 0);
                    target.setAttribute('data-y', 0);
                    App.ServiceSlider.changeActiveClassCanvasSlider(nextIndex);
                    App.ServiceSlider.changeNavColor();
                    $('.draw-circle-svg').fadeIn();
                    $('.scroll-video-progress').removeClass('dragDirection')
                    $('.draw-circle-svg-3').removeClass('allowDrag dragDirection draggable-services').removeAttr('style');
                    var newActiveElem = $('.scroll-video-progress').eq(App.DragServices.draggableRange['nextIndex']);
                    $(newActiveElem).addClass('active').siblings().removeClass('active');

                    App.ServiceSlider.setDragIconToTheActiveDragPoint(App.DragServices.draggableRange['nextIndex']);
                }, 1700 )

              }else{

                //set red progress to 0
                App.ServiceSlider.resetRedLine(App.ServiceSlider.getActiveIndex(), 0);
                
                  $('.scroll-video-progress').eq(App.DragServices.draggableRange['nextIndex']).find('.draw-circle-svg').fadeIn();
                  App.ServiceSlider.resetDragEndPointsForDirection(App.ServiceSlider.getActiveIndex());
                  App.ServiceSlider.drawLeftImagesDragProgress(App.ServiceSlider.drawnImageNumber, startImg)
                  TweenMax.to($('.draggable-services.allowDrag'), 1, {x:0,y:0, ease:Sine.easeOut});
                  target.setAttribute('data-x', 0);
                  target.setAttribute('data-y', 0);
              }

          }else{

            App.ServiceSlider.drawnImageNumber = App.ServiceSlider.drawnImageNumber - drawImageNumber;

            // if drag direction is from right to left 
            App.DragServices.draggableRange = App.ServiceSlider.getRange();
            var prevIndex  = App.DragServices.draggableRange['prevIndex'],
                activeIndex = App.ServiceSlider.getActiveIndex(),
                activeImgNum = parseInt($($('.scroll-video-progress').eq(activeIndex)).data('progress')),
                prevImgNum = parseInt($($('.scroll-video-progress').eq(prevIndex)).data('progress'))

            if( x <= minDraggableWidth/2 ){
              $('.scroll-video-progress-dashes-active').eq(prevIndex).removeClass('left')
              App.ServiceSlider.resetRedLine(prevIndex, 0);
              $('.draw-circle-svg').eq(activeIndex).fadeIn();

              //prevent dragging, during slide animation
              $('.draw-circle-svg-3').addClass('prevent');
              
              TweenMax.to($('.draggable-services.allowDrag'), 1, {x:minDraggableWidth, y:0, ease:Sine.easeOut});
              App.ServiceSlider.resetDragEndPointsForDirection(prevIndex);
              App.ServiceSlider.allowDrag = false;
              App.ServiceSlider.drawLeftImagesDragProgress(App.ServiceSlider.drawnImageNumber, prevImgNum);
              App.ServiceSlider.reverseSlideAnimation(parseFloat(activeImgNum));

              //PLAY
              App.ServiceSlider.timerReverse = setTimeout(function(){
                
                  App.ServiceSlider.allowDrag = true;
                  App.ServiceSlider.playSlideAnimation(prevImgNum);
                  target.setAttribute('data-x', 0);
                  target.setAttribute('data-y', 0);
                  App.ServiceSlider.changeActiveClassCanvasSlider(prevIndex);
                  App.ServiceSlider.changeNavColor();
                  $('.scroll-video-progress').removeClass('dragDirection')
                  $('.draw-circle-svg-3').removeClass('allowDrag dragDirection draggable-services').removeAttr('style');
                  var newActiveElem = $('.scroll-video-progress').eq(App.DragServices.draggableRange['prevIndex']);
                  $(newActiveElem).addClass('active').siblings().removeClass('active');
                  App.ServiceSlider.setDragIconToTheActiveDragPoint(App.DragServices.draggableRange['prevIndex']);
              }, 1700 )
            }else{
              
               App.ServiceSlider.resetRedLine(prevIndex, 1);
                $('.scroll-video-progress').eq(App.DragServices.draggableRange['prevIndex']).find('.draw-circle-svg').fadeIn();
                App.ServiceSlider.resetDragEndPointsForDirection(App.ServiceSlider.getActiveIndex());
                App.ServiceSlider.drawLeftImagesDragProgress(App.ServiceSlider.drawnImageNumber, activeImgNum)
                TweenMax.to($('.draggable-services.allowDrag'), 1, {x:0,y:0, ease:Sine.easeOut});
                target.setAttribute('data-x', 0);
                target.setAttribute('data-y', 0);
            }
          }
        }
        
        // this is used later in the resizing and gesture demos
        // window.drag = drag;

  }


  ,checkDirection : function(x){
    if( x>=0 ){
      App.DragServices.dragDirection = "right";
    }else{
      App.DragServices.dragDirection = "left";
    }
  }

}
