App.DropDown = {

    init :function(){
        if(!$('.dd-box')) return;
        App.DropDown.bind();
    }

    ,bind : function(){
        $(document).on('click', '.dd-title', function(){
            App.DropDown.animateAccordion($(this));
        });  
    }

    ,animateAccordion : function($this){
        var $parent = $this.parents('.dd-box');
        var $content = $parent.find('.dd-text');
        //Close the others drop down, if open
        $parent.siblings().removeClass('open')
        $parent.siblings().find('.dd-text').removeClass('open')

        if( $($parent).length ){
            if( !$($parent).hasClass("open")){
                TweenMax.to( $content, 1.2, {
                    className:"+=open",
                    ease: Quad.easeOut
                });

                $($parent).addClass('open')
            } else {
                TweenMax.to($content, 1, {
                    className: "-=open",
                    ease:  Quad.easeOut
                });
                $($parent).removeClass('open')
            }
        }
    }
}

