App.fadeGallery = {

    /** PLAY IF IT'S IN THE VIEWPORT 
     *  AND BIND ON SCROLL EVENT TO PLAY/STOP IF IT'S VISIBLE */
    init : function(){
        if($('.anim-blink-gallery').visible( true, true)){
            App.fadeGallery.galleryShown = true;
            App.fadeGallery.galleryTimer = setInterval(function(){ App.fadeGallery.play(); }, 1000);
        }else{
            clearInterval(App.fadeGallery.galleryTimer);
        }
        App.fadeGallery.bind();
    }

    ,galleryTimer  :null
    ,galleryShown : false
    ,bind : function(){
        $(window).on('scroll', function () {
            //if it's in the viewport call play function once
            // play function - change which container to fade through 300ms
            if($('.anim-blink-gallery').visible(true, true)){
                if( App.fadeGallery.galleryShown ) return;
                App.fadeGallery.galleryShown = true;
                App.fadeGallery.galleryTimer = setInterval(function(){ App.fadeGallery.play(); }, 1000);
            }else{
                //if it's not in the viewport clear playing
                App.fadeGallery.galleryShown = false;
                clearInterval(App.fadeGallery.galleryTimer);
            }
        })
    }

    /** GET COUNT OF IMAGE CONTAINERS 
     *  GET RANDOM NUMBER BETWEEN 0 AND CONTAINER'S COUNT
     *  CHANGE IMAGE OF THIS CONTAINER
     */
    ,play : function(){
        var boxCount = $('.img-box-blink-container').length;
        var randomContainerIndex = App.fadeGallery.getRandomContainer(boxCount);
        App.fadeGallery.changeActiveImage(randomContainerIndex);
    }


    ,getRandomContainer : function(maxRange){
        return Math.floor(Math.random() * maxRange) + 0 ;
    }

    ,getCurrentNextImage : function(randomContainerIndex){
        var box = $('.img-box-blink-container').eq(randomContainerIndex);
        if($(box).hasClass('disable')) return false;
        var currentActiveImg = $(box).find('img.active');
        var nextActiveImg;
        
        if($(currentActiveImg).next('img')){
            nextActiveImg = $(currentActiveImg).next('img');
            if(!nextActiveImg.length){
                nextActiveImg = $($(box).find('img:first'))
            }
        }
   
        if(!$(currentActiveImg).length || !$(nextActiveImg).length) return false;
        if($(currentActiveImg).get(0) === $(nextActiveImg).get(0)) return false;
        return [currentActiveImg, nextActiveImg]
    }

    /** FADE CURRENT ACTIVE IMAGE TO 0 
     *  AND ANIMATE OPACITY OF THE NEXT IMAGE TO 1 
     *  IF THERE IS NO NEXT IMAGE< NEXT WILL BE THE FIRST ONE
     * */
    ,changeActiveImage : function(randomContainerIndex){
        var images = App.fadeGallery.getCurrentNextImage(randomContainerIndex);
    
        if(!images) return;
        
        var img_0 = images[0];

        var tl = new TimelineMax({
            onStart : function(){
                $($(img_0).parent('.img-box-blink-container')).addClass("disable");
            }
            ,onComplete : function(){
                $($(img_0).parent('.img-box-blink-container')).removeClass("disable");
            }
        });

        tl.to(images[0], 3.5, {opacity:0, className:"-=active", ease:Power2.easeNone},0)
        tl.fromTo(images[1], 4.5, {opacity:0},{opacity:App.fadeGallery.randomOpacity(), className:"+=active", ease:Power0.easeOut},1.9)
    }

    ,randomOpacity : function(){
        var arr_opacity = [0.9, 0.6, 0.8, 1][Math.floor(Math.random() * 4)];
        return arr_opacity;
    }

} 
App.Form = {



    init : function(){
        App.Form.bind();
    }

    ,bind : function(){
        $('.form').on('submit', function(e) {
            e.preventDefault();
            App.Form.send(e, $(this));
        });
    }

    ,send : function(e, $this){

        var form = $this;
            $.ajax({
                method: "POST",
                url: form.attr('action'),
                data: form.serialize(),
                dataType : "json"
            })
            .done(function(data) {

                $(".error").removeClass('error'); 
                $('.error-msg').text('');
                try {
                    if ( data.status == false ) { 
    
                        App.Form.resetErrorstFields(form);
                        $('.success-msg').fadeOut();
                        App.Form.showErrors(form, data.errors);
                        App.Form.scrollToElem($($('.error')[0]),1000);
                        
                    } else {
                        form.find('select').prop("selectedIndex", 0);
                        $(form).find('input[type=checkbox]').prop('checked', false);
                        $('.g-recaptcha').removeClass('error');
                        App.Form.resetErrorstFields( form );
                        App.Form.showSuccessMsg();
                        $(form)[0].reset();
                    }
                } catch(e) {}
            })
            .fail(function() {})
            .always(function() {});
    }


    ,resetErrorstFields : function(form){
        var field = $(form).find(".js-field");
        var fieldBox = $(form).find(".js-field-box");
        var error = $(form).find(".error-field");

        $(fieldBox).removeClass("error");
        $(error).text("");
        $(error).val("");
        if ($(form.find('.g-recaptcha')).length) {
            grecaptcha.reset();
        }
    }



    ,scrollToElem : function(elem, seconds){
        $('html, body').animate({
            scrollTop: elem.offset().top
        }, seconds);
    }



    ,showSuccessMsg : function(){
        TweenMax.fromTo('.success-msg', 1, { autoAlpha: 0, yPercent: -2 }, {
            autoAlpha: 1,
            yPercent: 0,
            ease: Expo.easeOut,
            onStart: function() {
                TweenMax.set('.success-msg', { css: { display: 'block' } })
            }
        })
    }


    ,showErrors : function(form, errors){
        $.each(errors, function(key, field){
            $(form).find("[data-id='" + key + "']").parents('.js-field-box').addClass('error'); 
            $(form).find("[data-id='" + key + "']").parents('.js-field-box').find('.error-field').text(field)
        })
    }
}
