    ,getVideoSrc : function(currVid){
        var src;
        //Get mobile video
        if($(window).width()<1024){
            if($(currVid).data("mobile")){
                src = $(currVid).data("mobile");
            }else{
                src = $(currVid).data("src");
            }
        //Get desktop video
        }else{
            if($(currVid).data("mobile")){
                src = $(currVid).data("desktop");
            }else{
                src = $(currVid).data("src");
            }
        }
        return src;
    }


