    setHeightCanvasConatainer : function(){
        if($(window).width()>1400){
            $('.video-container').height(App.ServiceSlider.ratioH + 200);
        }else{
            $('.video-container').height(App.ServiceSlider.ratioH + 50);
        }
        
    }


    ,drawImageInCanvas : function (newLocation) {
        var canv = document.getElementById('scrollVideo');
        var context = canv.getContext('2d');
        if (App.ServiceSlider.drawnLocation == newLocation && newLocation !== 1) return;
        App.ServiceSlider.drawnLocation = newLocation;

        if(newLocation> App.ServiceSlider.imgPos_4 || isNaN(newLocation)) return;
        
        //DRAW FIRST IMAGE
        if(newLocation ==0 ){ 
            context.drawImage(App.ServiceSlider.images[0], 0, 0, 900, 800);
        //DRAW LAST IMAGE
        }else if(newLocation > App.ServiceSlider.totalImages){ 
            context.drawImage(App.ServiceSlider.images[App.ServiceSlider.totalImages-1], 0, 0, 900, 800);
        }
        //DRAW CURRENT IMAGE ACCORDING TO SCROLL PROGRESS
        else{ 
            context.drawImage(App.ServiceSlider.images[newLocation-1], 0, 0, 900, 800);
        } 
    }

    ,canvasFillWin : function() {
        var canv = document.getElementById('ServiceSlider');
        var h = 800;
        var w = 900;
        var ratio = h/w;
        var winW = 900;
        var winH = 800;
        var winRatio = winH / winW;
        App.ServiceSlider.ratioH = winW * ratio;
        if(winRatio > ratio) {
            $(canv)
            .width(winH / ratio)
            .height(winH);

        } else {
            $(canv)
            .width(winW)
            .height(winW * ratio);
        }
    }

    ,totalImages : null
    ,images: null
    ,canvasImageHasLoaded : []
    ,loadCanvasImages : function(){
        $('.hidden-layer').show();
        var canvas = $('#scrollVideo'),
            totalImages =  parseInt(canvas.data("img-count")),
            imgUrl = canvas.data("img-src"),
            images = [];
        for(var i = 0; i < App.ServiceSlider.imgPos_2 +1; i++) {
            // FILENAME OF THE FRAMES
            App.ServiceSlider.canvasImageHasLoaded[i] = false;
            var filename = 'j_00';
            if(i < 10) filename += '00';
            else if(i < 100) filename += '0';
            else if(i == 100) filename += '';
            filename += i + '.jpg';
            var img = new Image;
            img.src = imgUrl + filename;
            images.push(img);
            document.getElementById("hiddenImages").appendChild(img);
            if(i== App.ServiceSlider.imgPos_2 - 1){
                App.ImagesLoaded.init();
            }
        }
        App.ServiceSlider.totalImages = totalImages;
        App.ServiceSlider.images = images;
    }


    ,loadLeftCanvasImages : function(){
        var canvas = $('#scrollVideo'),
            totalImages =  parseInt(canvas.data("img-count")),
            imgUrl = canvas.data("img-src"),
            images = [];
        for(var i = App.ServiceSlider.imgPos_2; i < totalImages; i++) {
            // FILENAME OF THE FRAMES
            App.ServiceSlider.canvasImageHasLoaded[i] = false;
            var filename = 'j_00';
            if(i < 10) filename += '00';
            else if(i < 100) filename += '0';
            else if(i == 100) filename += '';
            filename += i + '.jpg';
            var img = new Image;
            img.src = imgUrl + filename;
            images.push(img);
            document.getElementById("hiddenImagesLeft").appendChild(img);
            if(i== App.ServiceSlider.imgPos_4 - 1){
                App.ImagesLoaded.loadLeftServiceImages()
            }
            
        }
        App.ServiceSlider.totalImages = totalImages;
        App.ServiceSlider.images = App.ServiceSlider.images.concat(images);
    }


    ,animateMobileVideoSection :function(){
        if($(".scroll-video-content").length && $(window).width()<1025){
            var controller = new ScrollMagic.Controller(),
                tween = new TimelineMax(),
                el  = $('.scroll-video-content');

                tween.fromTo($(el).find('.anim_s_t'),0, {opacity:0}, {opacity:1}, 0.1)
                tween.staggerFromTo( $($(el).find('.anim_s_t')).find('.char'),0.4, {opacity:0}, {opacity:1, ease:Power4.easeOut}, 0.035, 0.5)
                tween.fromTo($(el).find('.anim_s_sub_t'), 1, {opacity:0}, {opacity:1, ease:Sine.easeOut}, "-=0.5")
                tween.fromTo($(el).find('.anim_s_txt'), 1, {opacity:0}, {opacity:1,  ease:Sine.easeOut}, "-=0.4")
                tween.fromTo($(el).find('.anim_s_txt_2'), 1, {opacity:0}, {opacity:1, ease:Sine.easeOut}, "-=0.4")
                new ScrollMagic.Scene({
                    triggerElement:  el,         
                    reverse: false,  
                    triggerHook: "onEnter",
                    offset:  getViewportSize().height * 0
                })
                .setTween(tween)
                .addTo(controller);
        }
    }


